CREATE OR REPLACE FUNCTION pls_obter_nota_credito_titulo ( nr_titulo_rec_p bigint) RETURNS varchar AS $body$
DECLARE


ds_nota_credito_w		varchar(4000)	:= null;
nr_seq_nota_credito_w		bigint;

C01 CURSOR FOR
	SELECT	distinct a.nr_sequencia
	from	nota_credito a
	where	a.nr_titulo_receber = nr_titulo_rec_p;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_nota_credito_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (ds_nota_credito_w IS NOT NULL AND ds_nota_credito_w::text <> '') then
		ds_nota_credito_w	:= substr(ds_nota_credito_w || ', ' || nr_seq_nota_credito_w,1,4000);
	else
		ds_nota_credito_w	:= substr(nr_seq_nota_credito_w,1,4000);
	end if;
	end;
end loop;
close C01;

ds_nota_credito_w := substr(ds_nota_credito_w,1,length(ds_nota_credito_w));

return	ds_nota_credito_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
