CREATE OR REPLACE FUNCTION pls_obter_nr_atendimento ( nr_seq_atendimento_p bigint, ie_tipo_p text) RETURNS bigint AS $body$
DECLARE

/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:   Obter o número do atendimento das tabelas 'MPREV_ATENDIMENTO' e 'MPREV_ATEND_PACIENTE'
----------------------------------------------------------------------------------------------------------------------------------------------------

Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ] Relatórios [X] Outros: HTML5
 ----------------------------------------------------------------------------------------------------------------------------------------------------

Pontos de atenção:

Parâmetro ie_tipo_p:
	
'HDM'- Número do atendimento na função 'HDM - Atendimento'.
'EUP' - Número do atendimento na função 'Entrada Única de Pacientes'

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
*/ds_retorno_w	bigint;


BEGIN
if (ie_tipo_p = 'HDM') then
	select	max(nr_sequencia)
	into STRICT	ds_retorno_w
	from	mprev_atendimento
	where	nr_seq_pls_atend = nr_seq_atendimento_p;
elsif (ie_tipo_p = 'EUP') then
	select	max(a.nr_atendimento)
	into STRICT	ds_retorno_w
	from	mprev_atend_paciente a,
		mprev_atendimento b   
	where	b.nr_sequencia = a.nr_seq_atendimento
	and	b.nr_seq_pls_atend = nr_seq_atendimento_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_nr_atendimento ( nr_seq_atendimento_p bigint, ie_tipo_p text) FROM PUBLIC;

