CREATE OR REPLACE FUNCTION pls_obter_nr_fatura ( nr_seq_fatura_p ptu_fatura.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE

nr_fatura_w	ptu_fatura.nr_fatura%type;

BEGIN

if (nr_seq_fatura_p IS NOT NULL AND nr_seq_fatura_p::text <> '') then
	select	max(z.nr_fatura)
	into STRICT	nr_fatura_w
	from	ptu_fatura z
	where	z.nr_sequencia = nr_seq_fatura_p;
end if;

return	nr_fatura_w;

end;
$body$
