SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_nr_seq_prestador (cd_pessoa_fisica_p text, cd_cgc_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_prestador_w 	varchar(14);
nr_seq_prest_chamada_w	bigint;

BEGIN

	select	min(nr_seq_prestador)
	into STRICT	nr_seq_prest_chamada_w
	from	pls_prestador_medico
	where	cd_medico = cd_pessoa_fisica_p;

	if (nr_seq_prest_chamada_w IS NOT NULL AND nr_seq_prest_chamada_w::text <> '') then
		begin

		select	max(nr_sequencia)
		into STRICT	nr_seq_prestador_w
		from	pls_prestador_medico
		where	nr_seq_prestador  = nr_seq_prest_chamada_w
		and	cd_medico         = cd_pessoa_fisica_p;

		end;
	else
		begin
		if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
			begin
			select	min(nr_sequencia)
			into STRICT	nr_seq_prestador_w
			from	pls_prestador
			where	cd_pessoa_fisica = cd_pessoa_fisica_p;
			end;
		elsif (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then
			begin
			select	min(nr_sequencia)
			into STRICT	nr_seq_prestador_w
			from	pls_prestador
			where	cd_cgc = cd_cgc_p;
			end;
		end if;

		end;
	end if;
return	nr_seq_prestador_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
