CREATE OR REPLACE FUNCTION pls_obter_nr_telefone ( nr_ddi_telefone_p text, nr_ddd_telefone_p text, nr_telefone_p text ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w varchar(255);
nr_ddi_telefone_w varchar(255);			


BEGIN
	if (nr_telefone_p IS NOT NULL AND nr_telefone_p::text <> '') then
		if (substr(nr_ddi_telefone_p,1,1) = '+') then
			nr_ddi_telefone_w := nr_ddi_telefone_p;
		else
			nr_ddi_telefone_w := '+' || nr_ddi_telefone_p; 	
		end if;
		select CASE WHEN coalesce(nr_ddi_telefone_p::text, '') = '' THEN              nr_telefone_p  ELSE nr_ddi_telefone_w || ' ' || ' (' || nr_ddd_telefone_p || ') ' ||             case                when length(nr_telefone_p) = 9 then                    substr(nr_telefone_p, 1, 5) || '-' || substr(nr_telefone_p, 6, 9)                else                    substr(nr_telefone_p, 1, 4) || '-' || substr(nr_telefone_p, 5, 8)                end END
		into STRICT ds_retorno_w
		;
	end if;	
	return	ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
