SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_obervacao_titulo ( nr_titulo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

begin
select	a.ds_observacao_titulo
into STRICT	ds_retorno_w
from	titulo_receber		a,
	pls_mensalidade		b,
	pls_contrato_pagador	c,
	pls_contrato_pagador_fin d
where	a.nr_seq_mensalidade	= b.nr_sequencia
and	b.nr_seq_pagador	= c.nr_sequencia
and	d.nr_seq_pagador	= c.nr_sequencia
and	a.nr_titulo		= nr_titulo_p
and	a.dt_emissao between d.dt_inicio_vigencia and fim_dia(coalesce(d.dt_fim_vigencia,a.dt_emissao))
and	d.ie_destacar_reajuste	= 'S';
exception
when others then
	ds_retorno_w	:= null;
end;

if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
	ds_retorno_w	:= 'Percentual de Reajuste 6,73% conforme Oficio nº1065/2010/GGEFP/DIPRO, Protocolo nº33902.127824/2010-00 de 20/07/2010 entre 05/2010 e 04/2011';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_obervacao_titulo ( nr_titulo_p bigint) FROM PUBLIC;

