CREATE OR REPLACE FUNCTION pls_obter_obser_nota_credito ( nr_seq_mensalidade_seg_p bigint) RETURNS varchar AS $body$
DECLARE


ds_observacao_w		varchar(255);
nr_seq_nota_credito_w	bigint;


BEGIN

select	max(nr_seq_nota_credito)
into STRICT	nr_seq_nota_credito_w
from	pls_mensalidade_seg_item
where	nr_sequencia	= nr_seq_mensalidade_seg_p;

if (nr_seq_nota_credito_w IS NOT NULL AND nr_seq_nota_credito_w::text <> '') then
	select	substr(ds_observacao,1,255)
	into STRICT	ds_observacao_w
	from	nota_credito
	where	nr_sequencia	= nr_seq_nota_credito_w;
end if;

return	ds_observacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
