CREATE OR REPLACE FUNCTION pls_obter_obs_glosa_aut ( nr_seq_glosa_oc_p bigint, ie_tipo_consulta_p text, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE

/*
ie_tipo_consulta_p
G - Glosa
O - Ocorrência

ie_tipo_p
A - Autorização
R - Requisição

*/
ds_retorno_w	varchar(4000);


BEGIN

if (ie_tipo_consulta_p	= 'G') then
	select	ds_observacao
	into STRICT	ds_retorno_w
	from	pls_guia_glosa_ocorrencia_v
	where	nr_sequencia	= nr_seq_glosa_oc_p
	and		ie_tipo			= ie_tipo_consulta_p;
elsif (ie_tipo_consulta_p	= 'O') then
	select  b.ds_documentacao
	into STRICT	ds_retorno_w
	from    pls_ocorrencia       b,
			pls_ocorrencia_benef a
	where   a.nr_sequencia       = nr_seq_glosa_oc_p
	and     a.nr_seq_ocorrencia  = b.nr_sequencia;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
