CREATE OR REPLACE FUNCTION pls_obter_obs_glosa ( nr_seq_glosa_ref_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(4000);


BEGIN

select	max(substr(ds_observacao,1,4000))
into STRICT	ds_retorno_w
from	pls_conta_glosa
where	nr_sequencia = nr_seq_glosa_ref_p;

return	ds_retorno_w;

end;
