CREATE OR REPLACE FUNCTION pls_obter_oc_estrutura ( nr_seq_estrutura_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_cod_ocorrencia_w		varchar(4000);
ds_cod_ocorrencia_ww		varchar(15);

C01 CURSOR FOR
	SELECT	b.cd_ocorrencia
	from	pls_ocorrencia_regra_item	a,
		pls_ocorrencia			b
	where	nr_seq_estrutura_p	=	a.nr_seq_estrutura
	and	a.nr_seq_ocorrencia	=	b.nr_sequencia
	
union

	SELECT	b.cd_ocorrencia
	from	pls_ocorrencia_regra 		a,
		pls_ocorrencia			b
	where	a.nr_seq_estrutura	=	nr_seq_estrutura_p
	and	a.nr_seq_ocorrencia	=	b.nr_sequencia;


BEGIN
if (nr_seq_estrutura_p IS NOT NULL AND nr_seq_estrutura_p::text <> '') 	then
open C01;
loop
fetch C01 into
	ds_cod_ocorrencia_ww;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (ds_cod_ocorrencia_ww IS NOT NULL AND ds_cod_ocorrencia_ww::text <> '')	then
		ds_cod_ocorrencia_w := ds_cod_ocorrencia_w||ds_cod_ocorrencia_ww||chr(13)||chr(10);
	end if;
	end;
end loop;
close C01;
end if;
return	ds_cod_ocorrencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
