CREATE OR REPLACE FUNCTION pls_obter_ordem_benef_propop ( nr_seq_titular_p bigint, ie_opcao_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_benef_w		bigint;


BEGIN

select	CASE WHEN ie_reg=ie_opcao_p THEN nr_sequencia  ELSE null END
into STRICT	nr_seq_benef_w
from (
		SELECT	nr_sequencia,
			row_number() OVER () AS ie_reg
		from	pls_proposta_beneficiario
		where	nr_seq_titular = nr_seq_titular_p) alias0
where	ie_reg		= ie_opcao_p;

return	nr_seq_benef_w;

end;
