SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_origem_carencia ( nr_seq_benef_proposta_p bigint, nr_seq_plano_p bigint) RETURNS varchar AS $body$
DECLARE


cont_plano_w	bigint;
cont_benef_w	bigint;
ds_retorno_w	varchar(50);


BEGIN

select	count(*)
into STRICT	cont_plano_w
from	pls_carencia
where	nr_seq_plano	= nr_seq_plano_p;

select	count(*)
into STRICT	cont_benef_w
from	pls_carencia
where	nr_seq_pessoa_proposta	= nr_seq_benef_proposta_p;

if (cont_plano_w > 0) then
	ds_retorno_w		:= 'Produto';
elsif (cont_benef_w > 0) then
	ds_retorno_w	:=	'Benenficiário da proposta';

else
	ds_retorno_w	:= 'Não possui origem';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
