SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_origem_copartic ( nr_seq_segurado_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_contrato_w		bigint;
nr_seq_plano_w			bigint;
ie_coparticipacao_w		varchar(1);
qt_contrato_w			bigint	:= 0;
qt_plano_w			bigint	:= 0;
ds_retorno_w			varchar(50)	:= '';


BEGIN

begin
select	nr_seq_contrato,
	nr_seq_plano
into STRICT	nr_seq_contrato_w,
	nr_seq_plano_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;
exception
	when others then
	nr_seq_contrato_w	:= 0;
	nr_seq_plano_w		:= 0;
end;

select	coalesce(max(ie_coparticipacao),'N')
into STRICT	ie_coparticipacao_w
from	pls_plano
where	nr_sequencia	= nr_seq_plano_w;

if (ie_coparticipacao_w = 'S') then
	select	count(*)
	into STRICT	qt_contrato_w
	from	pls_regra_coparticipacao
	where	nr_seq_contrato	= nr_seq_contrato_w;

	select	count(*)
	into STRICT	qt_plano_w
	from	pls_regra_coparticipacao
	where	nr_seq_plano	= nr_seq_plano_w
	and	coalesce(nr_seq_contrato::text, '') = ''
	and	coalesce(nr_seq_proposta::text, '') = '';

	if (qt_contrato_w > 0) then
		ds_retorno_w	:= 'Coparticipação (Contrato)';
	elsif (qt_plano_w > 0) then
		ds_retorno_w	:= 'Coparticipação (Plano)';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_origem_copartic ( nr_seq_segurado_p bigint) FROM PUBLIC;

