CREATE OR REPLACE FUNCTION pls_obter_origem_regra_ref ( nr_seq_regra_ref_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w	varchar(255);
			

BEGIN 
 
if (coalesce(nr_seq_regra_ref_p,-1)<> -1) then 
 
begin 
 
select	coalesce(max(Obter_Valor_Dominio(3519,ie_tipo_tabela)),'') 
into STRICT	ds_retorno_w 
from	pls_regra_preco_proc 
where	nr_sequencia = nr_seq_regra_ref_p;
 
exception 
when others then 
return null;
end;
 
end if;
 
return	ds_retorno_w;
 
end;
$body$
