CREATE OR REPLACE FUNCTION pls_obter_padrao_vend_canal ( nr_seq_vendedor_p integer) RETURNS integer AS $body$
DECLARE


nr_seq_vend_canal_w	integer(10);


BEGIN

select  	coalesce(max(nr_sequencia),0)
into STRICT		nr_seq_vend_canal_w
from    	pls_vendedor_vinculado
where   	nr_seq_vendedor = nr_seq_vendedor_p
and     	ie_situacao = 'A'
and     	ie_vendedor_padrao = 'S';

if (nr_seq_vend_canal_w = 0) then
	nr_seq_vend_canal_w := null;
end if;

return	nr_seq_vend_canal_w;

end 	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
