CREATE OR REPLACE FUNCTION pls_obter_pagador_benef ( nr_seq_segurado_p bigint, dt_referencia_p timestamp, ie_tipo_item_p text) RETURNS bigint AS $body$
DECLARE


/* 	IE_TIPO_ITEM_P:
	'3'	- Coparticipação
	'6' , '7'	- Pós estabelecido
	'13'	- Custo Operacional
*/
nr_seq_pagador_w	pls_contrato_pagador.nr_sequencia%type;


BEGIN
select	max(b.nr_seq_pagador_item)
into STRICT	nr_seq_pagador_w
from	pls_segurado_pagador	a,
	pls_pagador_item_mens	b
where	b.nr_seq_pagador	= a.nr_seq_pagador
and	a.nr_seq_segurado	= nr_seq_segurado_p
and	ie_tipo_item		= ie_tipo_item_p
and	a.dt_inicio_vigencia	<= dt_referencia_p
and	coalesce(a.dt_fim_vigencia,dt_referencia_p)	>= dt_referencia_p;

if (coalesce(nr_seq_pagador_w::text, '') = '') then
	select	max(a.nr_seq_pagador)
	into STRICT	nr_seq_pagador_w
	from	pls_segurado_pagador	a
	where	a.nr_seq_segurado	= nr_seq_segurado_p
	and	a.dt_inicio_vigencia	<= dt_referencia_p
	and	coalesce(a.dt_fim_vigencia,dt_referencia_p)	>= dt_referencia_p;

	if (coalesce(nr_seq_pagador_w::text, '') = '') then
		select	max(nr_seq_pagador)
		into STRICT	nr_seq_pagador_w
		from	pls_segurado
		where	nr_sequencia = nr_seq_segurado_p;
	end if;
end if;

return	nr_seq_pagador_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
