CREATE OR REPLACE FUNCTION pls_obter_pagador_fea ( nr_seq_segurado_p pls_sca_vinculo.nr_seq_segurado%type, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


nr_seq_pagador_w	pls_contrato_pagador.nr_sequencia%type	:= null;


BEGIN

select	max(d.nr_sequencia)
into STRICT	nr_seq_pagador_w
from	pls_bonificacao_vinculo	a,
	pls_plano		b,
	pls_congenere		c,
	pls_contrato_pagador 	d
where	b.nr_sequencia	= a.nr_seq_sca_seguro_obito
and	c.nr_sequencia	= b.nr_seq_congenere_obito
and	c.nr_sequencia	= d.nr_seq_congenere
and	a.nr_seq_segurado = nr_seq_segurado_p
and	dt_referencia_p between trunc(coalesce(a.dt_inicio_vigencia, dt_referencia_p),'dd') and fim_dia(coalesce(a.dt_fim_vigencia, dt_referencia_p));

if (coalesce(nr_seq_pagador_w::text, '') = '') then
	select	max(d.nr_sequencia)
	into STRICT	nr_seq_pagador_w
	from	pls_sca_vinculo	a,
		pls_plano	b,
		pls_congenere	c,
		pls_contrato_pagador d
	where	b.nr_sequencia	= a.nr_seq_plano
	and	c.nr_sequencia	= b.nr_seq_congenere_obito
	and	c.nr_sequencia	= d.nr_seq_congenere
	and	a.nr_seq_segurado = nr_seq_segurado_p
	and     (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and	b.ie_seguro_obito = 'B'
	and	dt_referencia_p between trunc(coalesce(a.dt_inicio_vigencia, dt_referencia_p),'dd') and fim_dia(coalesce(a.dt_fim_vigencia, dt_referencia_p));
end if;

return nr_seq_pagador_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
