SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_param_atend_geral ( ie_tipo_campo_p text) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Function utilizada para retornar os parâmetros marcados na função OPS - Gestão
de Operadoras / Parâmetros OPS  / Atendimento Operadora
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
IE_TIPO_CAMPO_P
FA - Função Autorizador
TT - Tempo de validade do token de atendimento (minutos).
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w		varchar(200);


BEGIN

--Função autorizador
if (ie_tipo_campo_p = 'FA') then
	select	coalesce(max(ie_funcao_autorizador),1)
	into STRICT	ds_retorno_w
	from	pls_param_atend_geral;

--Tempo do token
elsif (ie_tipo_campo_p = 'TT') then
	select	coalesce(max(qt_tempo_token),5)
	into STRICT	ds_retorno_w
	from	pls_param_atend_geral;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
