CREATE OR REPLACE FUNCTION pls_obter_param_faturamento ( ie_opcao_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter os dados da tabela pls_parametro_faturamento

-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  x ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
ie_opcao_p:
FP - apresentar fatura portal
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w	varchar(255);


BEGIN

if (ie_opcao_p = 'FP') then
	select	max(coalesce(ie_fat_portal,'N'))
	into STRICT	ds_retorno_w
	from	pls_parametro_faturamento;
end if;

return	ds_retorno_w;

end;
$body$
