CREATE OR REPLACE FUNCTION pls_obter_param_imp_cta ( cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Retorna o parametro solicitado pela opção

	Opções
	FV - Forma de validação
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------
Pontos de atenção:


Alterações:
-------------------------------------------------------------------------------------------------------------------
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w	varchar(1000) := '';


BEGIN

if (ie_opcao_p = 'FV') then

	select	coalesce(max(ie_forma_validacao),'P')
	into STRICT	ds_retorno_w
	from	pls_param_importacao_conta
	where	cd_estabelecimento	= cd_estabelecimento_p;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
