SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_parcelas_abertas_pag ( nr_seq_pagador_p text, dt_referencia_mens_p timestamp) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
Obter as parcelas em aberto de um pagador
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [X]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
Function utilizada no relatório CPLS-241 (Unimed Blumenau)

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_data_w		varchar(10);
ds_parcelas_abertas_w	varchar(500);

C01 CURSOR FOR
	SELECT	a.dt_referencia
	from	pls_mensalidade	a,
		titulo_receber	b
	where	a.nr_sequencia 		= b.nr_seq_mensalidade
	and	b.nr_seq_pagador	= nr_seq_pagador_p
	and	b.ie_situacao		= '1'
	and	dt_referencia_mens_p >= b.dt_pagamento_previsto
	and	coalesce(b.dt_pagamento_previsto,b.dt_vencimento) < trunc(clock_timestamp(),'dd')
	group by a.dt_referencia
	order by a.dt_referencia;

BEGIN

for r_c01_w in C01 loop
	ds_data_w := substr(to_char(r_c01_w.dt_referencia, 'month'),1,3)  || '/' || to_char(r_c01_w.dt_referencia, 'yyyy');

	ds_parcelas_abertas_w := ds_parcelas_abertas_w || ds_data_w || ', ';
end loop;

if (ds_parcelas_abertas_w IS NOT NULL AND ds_parcelas_abertas_w::text <> '') then
	ds_parcelas_abertas_w 	:= 	'ATENÇÃO: CONSTAM EM ABERTO ATÉ O PRESENTE MOMENTO, AS SEGUINTES PARCELAS:' || chr(13) ||
					substr(ds_parcelas_abertas_w, 1, (length(ds_parcelas_abertas_w) - 2)) || chr(13) ||
					'FAVOR ENTRAR EM CONTATO COM O SETOR FINANCEIRO.';
end if;

return	ds_parcelas_abertas_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_parcelas_abertas_pag ( nr_seq_pagador_p text, dt_referencia_mens_p timestamp) FROM PUBLIC;

