CREATE OR REPLACE FUNCTION pls_obter_parcela_sca ( nr_seq_vinculo_sca_p bigint, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
dt_sca_w		timestamp;
nr_parcela_w		bigint;


BEGIN

select	dt_inicio_vigencia
into STRICT	dt_sca_w
from	pls_sca_vinculo
where	nr_sequencia	= nr_seq_vinculo_sca_p;

select (trunc(months_between(trunc(dt_referencia_p,'month'),trunc(dt_sca_w,'month'))) + 1)
into STRICT	nr_parcela_w
;

ds_retorno_w	:= to_char(nr_parcela_w);

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
