CREATE OR REPLACE FUNCTION pls_obter_parecer_item_contes ( nr_seq_conta_p bigint, nr_seq_conta_item_p bigint, cd_codigo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255) := null;
nr_seq_item_w	bigint;


BEGIN

select	max(a.nr_sequencia)
into STRICT	nr_seq_item_w
from	pls_analise_conta_item	a,
	pls_analise_conta	b
where	b.nr_sequencia	= a.nr_seq_analise
and	a.nr_seq_conta	= nr_seq_conta_p
and (a.nr_seq_conta_proc	= nr_seq_conta_item_p
or	a.nr_seq_conta_mat	= nr_seq_conta_item_p)
and	a.cd_codigo		= cd_codigo_p;

if (nr_seq_item_w IS NOT NULL AND nr_seq_item_w::text <> '') then
	select	substr(ds_parecer,1,255)
	into STRICT	ds_retorno_w
	from	pls_analise_parecer_item
	where	nr_sequencia = (SELECT	max(nr_sequencia)
				from	pls_analise_parecer_item
				where	nr_seq_item = nr_seq_item_w);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
