SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_pendencia_grupo ( nm_usuario_p text, ie_origem_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000) := '0';
nr_seq_auditoria_w	bigint;
nr_seq_ordem_w		bigint;
nr_seq_grupo_ordem_w	bigint;

C01 CURSOR FOR
	SELECT	d.nr_sequencia,
		c.nr_seq_ordem
	from	pls_auditoria		d,
		pls_auditoria_grupo	c,
		pls_grupo_auditor	b,
		pls_membro_grupo_aud	a
	where	d.nr_sequencia		= c.nr_seq_auditoria
	and	c.nr_seq_grupo		= b.nr_sequencia
	and	b.nr_sequencia 		= a.nr_seq_grupo
	and	a.nm_usuario_exec 	= nm_usuario_p
	and	coalesce(c.dt_liberacao::text, '') = '';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_auditoria_w,
	nr_seq_ordem_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (ie_origem_p = 'P') then
		select	min(nr_seq_ordem)
		into STRICT	nr_seq_grupo_ordem_w
		from	pls_auditoria_grupo
		where	nr_seq_auditoria = nr_seq_auditoria_w
		and	coalesce(dt_liberacao::text, '') = '';
	elsif (ie_origem_p = 'M') then
		select	min(nr_seq_ordem)
		into STRICT	nr_seq_grupo_ordem_w
		from	pls_auditoria_grupo
		where	nr_seq_auditoria = nr_seq_auditoria_w
		and	nm_usuario_exec = nm_usuario_p;
	end if;

	if (nr_seq_grupo_ordem_w = nr_seq_ordem_w) then
		ds_retorno_w := ds_retorno_w || ',' || nr_seq_auditoria_w;
	end if;
	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
