CREATE OR REPLACE FUNCTION pls_obter_penden_grupo_conta ( nr_seq_analise_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1)	:= 'N';
nr_seq_grupo_w			bigint;
qt_reg_w			bigint;
nr_seq_grupo_atual_w		bigint;
nm_auditor_atual_w		varchar(255);
nr_seq_fluxo_maior_w		bigint;
ie_status_w			varchar(2);


BEGIN
select	max(ie_status)
into STRICT	ie_status_w
from	pls_analise_conta
where	nr_sequencia = nr_seq_analise_p;

if (ie_status_w in ('A', 'G')) then
	/*Obter o fluxo da vez*/

	select	min(nr_seq_ordem)
	into STRICT	nr_seq_fluxo_maior_w
	from	pls_auditoria_conta_grupo
	where	nr_seq_analise = nr_seq_analise_p
	and	coalesce(dt_liberacao::text, '') = '';

	/*Obter se entre os grupos de an√°lise que o auditor faz parte esta algum com o fluxo inferior*/

	select	CASE WHEN count(b.nr_seq_ordem)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	pls_membro_grupo_aud a,
		pls_auditoria_conta_grupo b
	where	a.nr_seq_grupo = b.nr_seq_grupo
	and	(((coalesce(b.nm_auditor_atual,'X') <> 'X') and (coalesce(b.nm_auditor_atual, nm_usuario_p) = nm_usuario_p))
	or	 ((coalesce(b.nm_auditor_atual,'X') = 'X') and (upper(a.nm_usuario_exec) = upper(nm_usuario_p))))
	and	b.nr_seq_analise = nr_seq_analise_p
	and	b.nr_seq_ordem = nr_seq_fluxo_maior_w
	and	a.ie_situacao = 'A';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
