SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_periodo_camara_comp ( nr_seq_camara_p bigint, qt_dias_antes_a500_p bigint, dt_prev_envio_p timestamp) RETURNS bigint AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:  Obter o período de câmara de compensação, de acordo com o calendário.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_camara_w			varchar(4000);
nr_seq_periodo_w		bigint;


BEGIN
select	max(nr_sequencia)
into STRICT	nr_seq_periodo_w
from (SELECT	a.nr_sequencia
	from	pls_camara_calendario b,
		pls_camara_calend_periodo a
	where	a.nr_seq_calendario	= b.nr_sequencia
	and	b.nr_seq_camara		= nr_seq_camara_p
	and	trunc(coalesce(dt_prev_envio_p,clock_timestamp()) + qt_dias_antes_a500_p,'dd') <= trunc(a.dt_limite_a500,'dd')
	order by a.dt_limite_a500) alias5 LIMIT 1;

if (coalesce(nr_seq_periodo_w::text, '') = '') then
	select	a.ds_camara
	into STRICT	ds_camara_w
	from	pls_camara_compensacao a
	where	a.nr_sequencia	= nr_seq_camara_p;

	CALL wheb_mensagem_pck.exibir_mensagem_abort(176421,'DS_CAMARA=' || ds_camara_w);
end if;

return	nr_seq_periodo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
