CREATE OR REPLACE FUNCTION pls_obter_permite_bio_consult ( cd_medico_p pessoa_fisica.cd_pessoa_fisica%type) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Validar se o medico esta apto a fazer checkin/checkout no consultorio.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta: 
[  ]  Objetos do dicionario [ ] Tasy (Delphi/Java) [ x] Portal [  ]  Relatorios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atencao:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
	

ds_retorno_w			varchar(1)	:= 'S';
	

BEGIN

--Verifica se o medico nao esta com participacao cirurgica em andamento.
select	CASE WHEN count(1)=0 THEN 'S'  ELSE 'N' END
into STRICT	ds_retorno_w
from	pls_exec_cirurg_bio_partic a,
	pls_exec_cirurg_biometria b,
	pls_exec_cirurgica_proc c
where	a.nr_seq_exec_cirurg_bio	= b.nr_sequencia
and	a.nr_seq_exec_cirurg_proc	= c.nr_sequencia
and	a.ie_status			= 'E'
and	c.ie_situacao_item		<> 'C'
and	b.cd_medico			= cd_medico_p
and	(b.dt_check_in IS NOT NULL AND b.dt_check_in::text <> '')
and	coalesce(b.dt_check_out::text, '') = '';

return  ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_permite_bio_consult ( cd_medico_p pessoa_fisica.cd_pessoa_fisica%type) FROM PUBLIC;

