SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_permite_just_prest (nr_seq_requisicao_p bigint) RETURNS varchar AS $body$
DECLARE

 
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade: Obter a origem e processo da requisiçã.  
------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ X ] Objetos do dicionário [ ] Tasy (Delphi/Java) [ ] Portal [ ] Relatórios [ ] Outros: 
 ------------------------------------------------------------------------------------------------------------------ 
Pontos de atenção: 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 
 
ie_permite_just_prest_w	varchar(1);
ie_tipo_processo_w	pls_requisicao.ie_tipo_processo%type;
ie_origem_solic_w	pls_requisicao.ie_origem_solic%type;


BEGIN 
 
if (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') then 
		 
	select	ie_tipo_processo, 
		ie_origem_solic 
	into STRICT	ie_tipo_processo_w, 
		ie_origem_solic_w 
	from 	pls_requisicao 
	where 	nr_sequencia = nr_seq_requisicao_p;
	--Verifica primeiro se o processo é pelo portal - wheb, "P". 
	if (ie_tipo_processo_w = 'P') then 
		ie_permite_just_prest_w := 'S';
	--Se não for, verifica se é Intercâmbio e origem do Portal. 
	elsif (ie_tipo_processo_w = 'I') and (ie_origem_solic_w = 'P') then 
		ie_permite_just_prest_w := 'S';
	else 
		ie_permite_just_prest_w := 'N';
	end if;
 
else	 
	ie_permite_just_prest_w := 'N';	
end if;
 
return	ie_permite_just_prest_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_permite_just_prest (nr_seq_requisicao_p bigint) FROM PUBLIC;

