CREATE OR REPLACE FUNCTION pls_obter_permite_repasse_web (nr_seq_congenere_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(1):='S';
qt_registros_w	bigint;

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Verificar se cooperativa possui regra de bloqueio de repasse.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  x] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
BEGIN

select	count(1)
into STRICT	qt_registros_w
from	pls_regra_bloqueio_repasse
where	nr_seq_congenere = nr_seq_congenere_p
and	dt_liberacao < clock_timestamp()
and (coalesce(dt_fim_bloqueio::text, '') = ''
or	dt_fim_bloqueio > clock_timestamp());

if (qt_registros_w > 0) then
	ds_retorno_w := 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
