CREATE OR REPLACE FUNCTION pls_obter_permite_solic_justif ( nr_seq_requisicao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w			varchar(2)	:= 'S';
nr_seq_prest_web_req_w		bigint;
nr_seq_prest_web_regra_w	bigint;
nr_seq_perfil_web_w		bigint;
qt_registros_w			bigint	:= 0;


BEGIN

if (coalesce(nr_seq_requisicao_p,0)	<> 0) then
	begin
		select	nr_seq_prestador_web
		into STRICT	nr_seq_prest_web_req_w
		from	pls_requisicao
		where	nr_sequencia	= nr_seq_requisicao_p;
	exception
	when others then
		nr_seq_prest_web_req_w	:= 0;
	end;

	if (coalesce(nr_seq_prest_web_req_w,0)	<> 0) then
		begin
			select	nr_seq_perfil_web
			into STRICT	nr_seq_perfil_web_w
			from	pls_usuario_web
			where	nr_sequencia	= nr_seq_prest_web_req_w;
		exception
		when others then
			nr_seq_perfil_web_w	:= 0;
		end;
	end if;

	if (pls_obter_se_controle_estab('RE') = 'S')then
		select	count(1)
		into STRICT	qt_registros_w
		from	pls_regra_bloq_just_aut
		where (coalesce(nr_seq_perfil_web::text, '') = '' or nr_seq_perfil_web	= nr_seq_perfil_web_w)
		and (coalesce(nr_seq_prestador_web::text, '') = ''	or nr_seq_prestador_web	= nr_seq_prest_web_req_w)
		and	clock_timestamp()			between dt_inicio_vigencia	and trunc(coalesce(dt_fim_vigencia,clock_timestamp()),'dd') + 86399/86400
		and (coalesce(cd_estabelecimento, wheb_usuario_pck.get_cd_estabelecimento) = wheb_usuario_pck.get_cd_estabelecimento)  LIMIT 1;
	else
		select	count(1)
		into STRICT	qt_registros_w
		from	pls_regra_bloq_just_aut
		where (coalesce(nr_seq_perfil_web::text, '') = '' or nr_seq_perfil_web	= nr_seq_perfil_web_w)
		and (coalesce(nr_seq_prestador_web::text, '') = ''	or nr_seq_prestador_web	= nr_seq_prest_web_req_w)
		and	clock_timestamp()			between dt_inicio_vigencia	and trunc(coalesce(dt_fim_vigencia,clock_timestamp()),'dd') + 86399/86400  LIMIT 1;
	end if;

	if (qt_registros_w	> 0) then
		ie_retorno_w	:= 'N';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
