CREATE OR REPLACE FUNCTION pls_obter_perm_fec_conta ( nr_seq_ocorrencia_benef_p bigint, nr_seq_conta_glosa_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'S';
ie_fechar_conta_oc_w	varchar(1);
ie_fechar_conta_glosa_w	varchar(1);


BEGIN

select	max(ie_fechar_conta)
into STRICT	ie_fechar_conta_oc_w
from	pls_ocorrencia_benef
where	nr_sequencia = nr_seq_ocorrencia_benef_p;

select	max(ie_fechar_conta)
into STRICT	ie_fechar_conta_glosa_w
from	pls_conta_glosa
where	nr_sequencia = nr_seq_conta_glosa_p;

if (coalesce(ie_fechar_conta_glosa_w,'S') = 'N') or (coalesce(ie_fechar_conta_oc_w,'S') = 'N') then
	ds_retorno_w := 'N';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
