CREATE OR REPLACE FUNCTION pls_obter_perm_valorizacao ( ie_tipo_regra_p bigint, nr_seq_regra_p bigint, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE


qt_permite_w		smallint;
ds_retorno_w		varchar(1);
ie_tipo_regra_w		pls_regra_lote_recalculo.ie_tipo_regra%type;


BEGIN

ds_retorno_w := 'N';

if (ie_tipo_regra_p IS NOT NULL AND ie_tipo_regra_p::text <> '') then

	select 	count(1)
	into STRICT	qt_permite_w
	from 	pls_cta_classif_acesso a,
		pls_cta_classif b
	where	a.nr_seq_classif = b.nr_sequencia
	and	b.ie_tipo_regra = ie_tipo_regra_p
	and	a.cd_perfil = cd_perfil_p;

	if (qt_permite_w > 0) then
			ds_retorno_w := 'S';
	else
		select 	count(1)
		into STRICT	qt_permite_w
		from 	pls_cta_classif_acesso a,
			pls_cta_classif b
		where	a.nr_seq_classif = b.nr_sequencia
		and	b.ie_tipo_regra = ie_tipo_regra_p;

		if (qt_permite_w > 0) then
			ds_retorno_w := 'N';
		else
			ds_retorno_w := 'S';
		end if;
	end if;

elsif (nr_seq_regra_p IS NOT NULL AND nr_seq_regra_p::text <> '') then

	select 	ie_tipo_regra
	into STRICT	ie_tipo_regra_w
	from	pls_regra_lote_recalculo
	where	nr_sequencia = nr_seq_regra_p;

	select 	count(1)
	into STRICT	qt_permite_w
	from 	pls_cta_classif_acesso a,
		pls_cta_classif b
	where	a.nr_seq_classif = b.nr_sequencia
	and	b.ie_tipo_regra = ie_tipo_regra_w
	and	a.cd_perfil = cd_perfil_p;

	if (qt_permite_w > 0) then
		ds_retorno_w := 'S';
	else
		select 	count(1)
		into STRICT	qt_permite_w
		from 	pls_cta_classif_acesso a,
			pls_cta_classif b
		where	a.nr_seq_classif = b.nr_sequencia
		and	b.ie_tipo_regra = ie_tipo_regra_w;

		if (qt_permite_w > 0) then
			ds_retorno_w := 'N';
		else
			ds_retorno_w := 'S';
		end if;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
