CREATE OR REPLACE FUNCTION pls_obter_pessoas_vinc_simul ( nr_seq_simulacao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1);
qt_pessoas_vinculadas_w		bigint;
qt_pessoas_simulacao_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_pessoas_vinculadas_w
from	pls_simulpreco_individual
where	nr_seq_simulacao	= nr_seq_simulacao_p
and	(cd_pessoa_fisica IS NOT NULL AND cd_pessoa_fisica::text <> '');

select	count(*)
into STRICT	qt_pessoas_simulacao_w
from	pls_simulpreco_individual
where	nr_seq_simulacao	= nr_seq_simulacao_p;

if (qt_pessoas_simulacao_w	= qt_pessoas_vinculadas_w) then
	ds_retorno_w	:= 'S';
elsif (qt_pessoas_simulacao_w	<> qt_pessoas_vinculadas_w) then
	ds_retorno_w	:= 'N';
end if;

return	ds_retorno_w;

end;
