SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_pessoa_chave_comp ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ds_aux_w		varchar(100);
cd_pessoa_fisica_w	varchar(14);
ds_chave_composta_w	varchar(500);
ds_chave_simples_w	varchar(255);
nm_tabela_w		varchar(30);


BEGIN

select	ds_chave_composta,
	ds_chave_simples,
	nm_tabela
into STRICT	ds_chave_composta_w,
	ds_chave_simples_w,
	nm_tabela_w
from	tasy_solic_alt_campo
where	nr_sequencia	= nr_sequencia_p;

if (nm_tabela_w	= 'PESSOA_FISICA') then
	ds_retorno_w	:= ds_chave_simples_w;
elsif (nm_tabela_w	= 'COMPL_PESSOA_FISICA') then
	select	substr(ds_chave_composta_w,position(('CD_PESSOA_FISICA=') in ds_chave_composta_w) + 17,length(ds_chave_composta_w))
	into STRICT	ds_aux_w
	;

	select	substr(ds_aux_w,1,position('#@#@' in ds_aux_w) -1)
	into STRICT	ds_retorno_w
	;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
