CREATE OR REPLACE FUNCTION pls_obter_pessoa_maior_16 ( dt_nascimento_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w            varchar(1) := 'S';
qt_idade_w		bigint;


BEGIN

if (dt_nascimento_p IS NOT NULL AND dt_nascimento_p::text <> '') then
	select	obter_idade(dt_nascimento_p, clock_timestamp(), 'A')
	into STRICT	qt_idade_w
	;
	if (qt_idade_w < 16 )then
		ds_retorno_w := 'N';
	end if;
end if;

return     ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
