CREATE OR REPLACE FUNCTION pls_obter_plano_benef_web ( nr_seq_tipo_acomodacao_p bigint, ie_abrangencia_intercambio_p text, ie_tipo_operacao_p text, nr_seq_congenere_p bigint, ie_origem_p text) RETURNS bigint AS $body$
DECLARE


nr_seq_plano_w	pls_plano.nr_sequencia%type	:= null;

C01 CURSOR FOR
	SELECT	a.nr_seq_plano
	from	pls_regra_plano_benef_web a,
		pls_plano b
	where	a.nr_seq_plano = b.nr_sequencia
	and (a.nr_seq_tipo_acomodacao = nr_seq_tipo_acomodacao_p or coalesce(a.nr_seq_tipo_acomodacao::text, '') = '')
	and (a.ie_abrangencia_intercambio = ie_abrangencia_intercambio_p or coalesce(a.ie_abrangencia_intercambio::text, '') = '')
	and	a.ie_tipo_operacao = ie_tipo_operacao_p
	and	coalesce(b.nr_seq_congenere,0) = coalesce(nr_seq_congenere_p, coalesce(b.nr_seq_congenere,0))
	and	((coalesce(a.ie_tasy,'S') = 'S' and ie_origem_p = 'T') or (coalesce(a.ie_portal,'S') = 'S'  and ie_origem_p = 'P') or (coalesce(a.ie_webservice,'N') = 'S' and ie_origem_p = 'W'))
	and	a.ie_situacao = 'A'
	and	coalesce(a.cd_estabelecimento, wheb_usuario_pck.get_cd_estabelecimento) = wheb_usuario_pck.get_cd_estabelecimento
	order by	coalesce(a.ie_abrangencia_intercambio,' '),
			coalesce(a.nr_seq_tipo_acomodacao,0);

C02 CURSOR FOR
	SELECT	a.nr_seq_plano
	from	pls_regra_plano_benef_web a,
		pls_plano b
	where	a.nr_seq_plano = b.nr_sequencia
	and (a.nr_seq_tipo_acomodacao = nr_seq_tipo_acomodacao_p or coalesce(a.nr_seq_tipo_acomodacao::text, '') = '')
	and (a.ie_abrangencia_intercambio = ie_abrangencia_intercambio_p or coalesce(a.ie_abrangencia_intercambio::text, '') = '')
	and	a.ie_tipo_operacao = ie_tipo_operacao_p
	and	coalesce(b.nr_seq_congenere,0) = coalesce(nr_seq_congenere_p, coalesce(b.nr_seq_congenere,0))
	and	((coalesce(a.ie_tasy,'S') = 'S' and ie_origem_p = 'T') or (coalesce(a.ie_portal,'S') = 'S'  and ie_origem_p = 'P') or (coalesce(a.ie_webservice,'N') = 'S' and ie_origem_p = 'W'))
	and		a.ie_situacao = 'A'
	order by	coalesce(a.ie_abrangencia_intercambio,' '),
			coalesce(a.nr_seq_tipo_acomodacao,0);

BEGIN

if (pls_obter_se_controle_estab('RE') = 'S') then
    for r_c01_w in C01 loop
        begin
        nr_seq_plano_w	:= r_c01_w.nr_seq_plano;
        end;
    end loop;
else
    for r_c02_w in C02 loop
        begin
        nr_seq_plano_w	:= r_c02_w.nr_seq_plano;
        end;
    end loop;
end if;

return	nr_seq_plano_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
