CREATE OR REPLACE FUNCTION pls_obter_plano_congenere ( nr_seq_congenere_p bigint) RETURNS bigint AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: A rotina é utilizada para buscar o produto cadastrado na congenere, só pode retornar se existir apenas um produto.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_plano_w	bigint;


BEGIN

begin
select	nr_sequencia
into STRICT	nr_seq_plano_w
from	pls_plano
where	nr_seq_congenere	= nr_seq_congenere_p;
exception
when others then
	nr_seq_plano_w	:= 0;
end;

return	nr_seq_plano_w;

end;
$body$
