CREATE OR REPLACE FUNCTION pls_obter_plano_item_visivel ( nm_tabsheet_p text, ie_tipo_operacao_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w			varchar(1)	:= 'S';


BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_retorno_w
from	pls_plano_item_operacao	b,
	pls_plano_item		a
where	a.nr_sequencia		= b.nr_seq_plano_item
and	a.ie_situacao		= 'A'
and	a.nm_tabsheet		= nm_tabsheet_p
and	b.ie_tipo_operacao	= ie_tipo_operacao_p;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
