SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_portador_lote_mens (nr_seq_lote_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(255);
/*
	CD - Código do Portador
	DS - Descrição do Portador
*/
BEGIN
ds_retorno_w := '';
if (ie_tipo_p = 'DS') then
	select	coalesce(max(b.ds_portador),'') ds
	into STRICT 	ds_retorno_w
	from	portador b
	where	b.cd_portador    = (SELECT 	max(x.cd_portador)
				    from 	pls_lote_mensalidade x
				    where 	x.nr_sequencia = nr_seq_lote_p);
elsif (ie_tipo_p = 'CD') then
	select	coalesce(max(b.cd_portador),'') cd
	into STRICT 	ds_retorno_w
	from	portador b
	where	b.cd_portador    = (SELECT 	max(x.cd_portador)
				    from 	pls_lote_mensalidade x
				    where 	x.nr_sequencia = nr_seq_lote_p);
end if;
return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
