CREATE OR REPLACE FUNCTION pls_obter_preco_item_pct ( nr_seq_preco_pacote_p bigint, nr_seq_prestador_p bigint, ie_tipo_guia_p text, ie_tipo_item_p text, ie_tipo_dp_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);


BEGIN
if (ie_tipo_item_p = 'P') then
	select	to_char(sum(coalesce(b.vl_negociado, b.vl_procedimento_regra)), 'FM999G999G999D90')
	into STRICT	ds_retorno_w
	from	procedimento a,
		pls_pacote_procedimento b
	where	b.ie_situacao	= 'A'
	and 	b.nr_seq_composicao = (	SELECT	max(c.nr_seq_composicao)
					from	pls_pacote_tipo_acomodacao c
					where	c.nr_sequencia = nr_seq_preco_pacote_p)
	and (b.nr_seq_prestador = nr_seq_prestador_p or coalesce(b.nr_seq_prestador::text, '') = '')
	and (b.ie_tipo_guia 	= ie_tipo_guia_p or coalesce(b.ie_tipo_guia::text, '') = '')
	and	a.cd_procedimento	= b.cd_procedimento
	and	a.ie_origem_proced	= b.ie_origem_proced
	and	a.cd_tipo_procedimento	= ie_tipo_dp_p;
elsif (ie_tipo_item_p = 'M') then
	select	to_char(sum(coalesce(b.vl_negociado, b.vl_material_regra)), 'FM999G999G999D90')
	into STRICT	ds_retorno_w
	from	pls_material a,
		pls_pacote_material b
	where	b.ie_situacao	 = 'A'
	and 	b.nr_seq_composicao = (	SELECT	max(c.nr_seq_composicao)
					from	pls_pacote_tipo_acomodacao c
					where	c.nr_sequencia = nr_seq_preco_pacote_p)
	and (b.nr_seq_prestador = nr_seq_prestador_p or coalesce(nr_seq_prestador::text, '') = '')
	and (b.ie_tipo_guia  = ie_tipo_guia_p or coalesce(ie_tipo_guia::text, '') = '')
	and	a.nr_sequencia	= b.nr_seq_material
	and	a.ie_tipo_despesa = ie_tipo_dp_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_preco_item_pct ( nr_seq_preco_pacote_p bigint, nr_seq_prestador_p bigint, ie_tipo_guia_p text, ie_tipo_item_p text, ie_tipo_dp_p bigint) FROM PUBLIC;

