CREATE OR REPLACE FUNCTION pls_obter_prestadores_usu_web ( nr_seq_usu_prestador_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_prestador_w		bigint;
ds_seq_prest_usu_web_w		varchar(1000);

C01 CURSOR FOR
	 SELECT a.nr_sequencia
	  from  pls_prestador a,
		pls_prestador_usuario_web b
	  where a.ie_situacao = 'A'
	  and   b.ie_situacao = 'A'
	  and   a.nr_sequencia = b.nr_seq_prestador
	  and   b.nr_seq_usuario = nr_seq_usu_prestador_p;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_prestador_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
		ds_seq_prest_usu_web_w := ds_seq_prest_usu_web_w||','||to_char(nr_seq_prestador_w);
	end;
end loop;
close C01;

ds_seq_prest_usu_web_w :=  substr(ds_seq_prest_usu_web_w, 2, length(ds_seq_prest_usu_web_w));

return	ds_seq_prest_usu_web_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
