SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_prestador_cnes ( cd_cnes_p text) RETURNS varchar AS $body$
DECLARE


cd_cgc_w		varchar(15);
ds_retorno_w		varchar(15);
nr_seq_prestador_w	varchar(10);


BEGIN

select	max(nr_sequencia)
into STRICT 	nr_seq_prestador_w
from 	pls_processo_prestador
where 	cd_cnes = cd_cnes_p;

if (coalesce(nr_seq_prestador_w::text, '') = '' )	then -- verifica prestador na tabela PJ
	select	max(cd_cgc)
	into STRICT 	cd_cgc_w
	from 	pessoa_juridica
	where 	cd_cnes = cd_cnes_p;
	if (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '')	then
		select	max(nr_sequencia)
		into STRICT 	nr_seq_prestador_w
		from 	pls_prestador
		where 	cd_cgc = cd_cgc_w;
	end if;
end if;

ds_retorno_w	:= nr_seq_prestador_w;

return ds_retorno_w;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
