CREATE OR REPLACE FUNCTION pls_obter_prestador_login ( nr_seq_usu_prestador_p bigint, nr_seq_prestador_p bigint) RETURNS varchar AS $body$
DECLARE

/*+++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter se o prestador está dentro de determinado login
--------------------------------------------------------------------------------------
Locais de chamada direta:
[X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ] Relatórios [ ] Outros:
 -------------------------------------------------------------------------------------
Pontos de atenção: sempre buscar o usuario prestador da tabela PLS_LOTE_PROTOCOLO_CONTA
+++++++++++++++++++++++++++++++++++++++++++++++*/ie_prest_valido_w	varchar(1);
qt_valido_w		bigint;


BEGIN

ie_prest_valido_w	:= 'N';

select	count(1)
into STRICT	qt_valido_w
from	pls_prestador_usuario_web	a
where	nr_seq_usuario	 = nr_seq_usu_prestador_p
and	nr_seq_prestador = nr_seq_prestador_p
and	ie_situacao	= 'A';

if (qt_valido_w > 0)	then
	ie_prest_valido_w	:= 'S';
end if;

return	ie_prest_valido_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
