CREATE OR REPLACE FUNCTION pls_obter_prestador_qualidade ( cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type, cd_cgc_p pessoa_juridica.cd_cgc%type) RETURNS varchar AS $body$
DECLARE


nm_prestador_w		pessoa_fisica.nm_pessoa_fisica%type;


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select	nm_pessoa_fisica
	into STRICT	nm_prestador_w
	from	pessoa_fisica
	where	cd_pessoa_fisica = cd_pessoa_fisica_p;

elsif (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then

	select	nm_fantasia
	into STRICT	nm_prestador_w
	from	pessoa_juridica
	where	cd_cgc = cd_cgc_p;

end if;

return	nm_prestador_w;

end;
$body$
