CREATE OR REPLACE FUNCTION pls_obter_prest_conferencia ( cd_cpf_cnpj_p pls_monit_tiss_confer.cd_cpf_cgc_prest_exec%type, ie_identificador_p pls_monit_tiss_confer.ie_identif_prest_exec%type) RETURNS PESSOA_JURIDICA.NM_FANTASIA%TYPE AS $body$
DECLARE


nm_prestador_w		pessoa_juridica.nm_fantasia%type;


BEGIN

if (ie_identificador_p = '1') then
	select	max(b.nm_fantasia)
	into STRICT	nm_prestador_w
	from	pls_prestador a,
		pessoa_juridica b
	where	a.cd_cgc = b.cd_cgc
	and	a.cd_cgc = cd_cpf_cnpj_p;
else
	select	max(nm_pessoa_fisica)
	into STRICT	nm_prestador_w
	from	pls_prestador a,
		pessoa_fisica b
	where	a.cd_pessoa_fisica = b.cd_pessoa_fisica
	and	b.nr_cpf = cd_cpf_cnpj_p;
end if;

return	nm_prestador_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
