CREATE OR REPLACE FUNCTION pls_obter_prest_solic_conta ( nr_seq_conta_p pls_conta.nr_sequencia%type, nr_seq_guia_p pls_conta.nr_seq_guia%type, nr_seq_prestador_p pls_conta.nr_seq_prestador%type) RETURNS bigint AS $body$
DECLARE

--Rotina responsável por trazer o prestador solicitante da conta  baseado na OS 643699, irá verificar primeiro na requisicao
--caso o não exista requisição ou este seja nulo, irá verificar na guia, e se ainda assim for nulo irá utilizar o prestador solicitante da conta
nr_seq_prest_solic_w		pls_requisicao.nr_seq_prestador%type;


BEGIN

select	max(c.nr_seq_prestador)
into STRICT	nr_seq_prest_solic_w
from	pls_requisicao c,
	pls_execucao_req_item a
where	a.nr_seq_requisicao	= c.nr_sequencia
and	a.nr_seq_guia		= nr_seq_guia_p;

if (coalesce(nr_seq_prest_solic_w::text, '') = '') then

	select	max(nr_seq_prestador)
	into STRICT	nr_seq_prest_solic_w
	from	pls_guia_plano
	where	nr_sequencia	= nr_seq_guia_p;

	if (coalesce(nr_seq_prest_solic_w::text, '') = '') then
		nr_seq_prest_solic_w	:= nr_seq_prestador_p;
	end if;
end if;

return	nr_seq_prest_solic_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_prest_solic_conta ( nr_seq_conta_p pls_conta.nr_sequencia%type, nr_seq_guia_p pls_conta.nr_seq_guia%type, nr_seq_prestador_p pls_conta.nr_seq_prestador%type) FROM PUBLIC;

