SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_prest_solic_rec ( nr_seq_conta_p pls_conta.nr_sequencia%type) RETURNS bigint AS $body$
DECLARE


nr_seq_prest_solic_w		pls_requisicao.nr_seq_prestador%type;
nr_seq_requisicao_w		pls_execucao_requisicao.nr_seq_requisicao%type;
nr_seq_guia_w			pls_conta.nr_seq_guia%type;


BEGIN
if (nr_seq_conta_p IS NOT NULL AND nr_seq_conta_p::text <> '') then
	/*Alterado para utilizar a informação do nr_seq_guia que esta salvo na conta drquadros O.S. 586937*/

	select	max(a.nr_seq_guia)
	into STRICT	nr_seq_guia_w
	from	pls_conta	a
	where	a.nr_sequencia = nr_seq_conta_p;

	if (nr_seq_guia_w IS NOT NULL AND nr_seq_guia_w::text <> '') then

		begin
			select	nr_seq_requisicao
			into STRICT	nr_seq_requisicao_w
			from	pls_execucao_requisicao
			where	nr_seq_guia	= nr_seq_guia_w;
		exception
		when others then
			nr_seq_requisicao_w := null;
		end;


		if (nr_seq_requisicao_w IS NOT NULL AND nr_seq_requisicao_w::text <> '') then
			select	max(nr_seq_prestador)
			into STRICT	nr_seq_prest_solic_w
			from	pls_requisicao
			where 	nr_sequencia	= nr_seq_requisicao_w;

		end if;

		if (coalesce(nr_seq_prest_solic_w::text, '') = '') then
			select 	max(nr_seq_prestador)
			into STRICT	nr_seq_prest_solic_w
			from	pls_guia_plano
			where	nr_sequencia	= nr_seq_guia_w;
		end if;
	end if;
end if;

return	nr_seq_prest_solic_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
