CREATE OR REPLACE FUNCTION pls_obter_proc_exige_ocor ( cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_conta_p bigint, nr_seq_proc_p bigint, nr_seq_guia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w			varchar(1) := 'N';


BEGIN

if (coalesce(nr_seq_proc_p,0) > 0) then

	/*Tratamento para procedimentos*/

	if (coalesce(nr_seq_guia_p,0) > 0) then

		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_retorno_w
		from	pls_guia_plano_proc
		where	nr_sequencia = nr_seq_proc_p
		and	cd_procedimento = cd_procedimento_p
		and	ie_origem_proced = ie_origem_proced_p;

	elsif (coalesce(nr_seq_conta_p,0) > 0) then

		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_retorno_w
		from	pls_conta_proc
		where	nr_sequencia = nr_seq_proc_p
		and	cd_procedimento = cd_procedimento_p
		and	ie_origem_proced = ie_origem_proced_p;

	end if;
else
	/*Tratamento para quando for verificado somente autorizações/conta*/

	if (coalesce(nr_seq_guia_p,0) > 0) then

		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_retorno_w
		from	pls_guia_plano_proc
		where	nr_seq_guia = nr_seq_guia_p
		and	cd_procedimento = cd_procedimento_p
		and	ie_origem_proced = ie_origem_proced_p;

	elsif (coalesce(nr_seq_conta_p,0) > 0) then

		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_retorno_w
		from	pls_conta_proc
		where	nr_seq_conta = nr_seq_conta_p
		and	cd_procedimento = cd_procedimento_p
		and	ie_origem_proced = ie_origem_proced_p;

	end if;
end if;

return	ie_retorno_w;

end;
$body$
