		pls_guia_plano b
	where	a.nr_seq_guia		= coalesce(nr_seq_guia_w,nr_seq_guia_p)
	and	a.nr_seq_guia		= b.nr_sequencia
	and	(a.ds_msg_externa IS NOT NULL AND a.ds_msg_externa::text <> '')
