	SELECT	substr(coalesce(substr(pls_obter_seq_codigo_material(b.nr_seq_material,''),1,255),b.nr_seq_material)||' - '||a.ds_msg_externa,1,255)
	from	pls_guia_glosa_ocorrencia_v a,
